function video_placer($vid) {
    global $collection;
    echo "<iframe class=\"vidcan\" src=\"http://player.vimeo.com/video/$vid";
    echo "?byline=0&amp;";
    echo "portrait=0&amp;";
    echo "color=ffffff\" width=\"500\" height=\"400\" frameborder=\"0\">";
    echo "</iframe>";
}
?>

