<script type="text/javascript">
$(document).ready(function() {
	var duration =    1e3 * 1;
	var timebetween = 1e3 * .3;
	$.fn.reverse = [].reverse;
	$('.vidcan').reverse().hide().each(function(i, e){
 		setTimeout(function(){
			$(e).show(duration);
 		}, i * timebetween)
	});
});
</script>